mapper.select:
SELECT m.matchup_id, m.category_id, c.category_name AS category_name, m.language_id, l.language_name AS language_name, m.selected_days, m.region_group, m.start_day, m.end_day, m.start_time, m.end_time, m.max_member, m.price, m.title, m.has_mento, (SELECT COUNT(*) FROM member_matchup mm WHERE mm.matchup_id = m.matchup_id AND mm.status = 'ACTIVE') AS matchup_count FROM matchup m LEFT JOIN category c ON m.category_id = c.category_id LEFT JOIN language l ON m.language_id = l.language_id
m.status = 'ACTIVE' AND m.region_group = #{regionGroup} AND m.category_id = #{categoryId} AND m.selected_days LIKE '%' || #{selectedDays} || '%' AND m.language_id = #{languageId}
ORDER BY m.start_day

mapper.update:
UPDATE MATCHUP
SET status = 'INACTIVE',
updated_at = sysdate
WHERE
MATCHUP_ID = #{matchupId}
AND LEADER_ID = #{leaderId}
